_questions = None
_answers = None


def QUESTIONS():
    global _questions
    if _questions is None:
        _questions = {}
        for index, question in CANONICAL_QUESTIONS.items():
            _questions[index] = question['question']
    return _questions


def ANSWERS():
    global _answers
    if _answers is None:
        _answers = {}
        for index, question in CANONICAL_QUESTIONS.items():
            for a_index, answer in question['answers'].items():
                _answers[a_index] = answer
    return _answers


